    <div class="taraf-map_floor">
      <div class="taraf-map_floor-choose" v-for="(item, index) in tarafs" :key="index">
        <button
            class="unfilled-button taraf-map_floor-choose-number"
            :class="{'activeFloor': item.floor === activeFloor}"
            @click="chooseFloor(item.floor)"
